$hex = $_GET["hex"];
if(strlen($hex) >= 1) {
    $r = $g = $b = $hex[0] . $hex[0];
}
if(strlen($hex) >= 3) {
    $r = $hex[0] . $hex[0];
    $g = $hex[1] . $hex[1];
    $b = $hex[2] . $hex[2];
}
if(strlen($hex) == 5) {
    $r = $g = $b = "00";
    $a = "ff";
}
if(strlen($hex) >= 6) {
    $r = $hex[0] . $hex[1];
    $g = $hex[2] . $hex[3];
    $b = $hex[4] . $hex[5];
}
if(strlen($hex) >= 8) {
    $a = $hex[6] . $hex[7];
}
$r = hexdec($r);
$g = hexdec($g);
$b = hexdec($b);
$a = hexdec($a);



$cc = $_GET["cc"];
$valid = false;
$validList = array("sRGB_HEX", "sRGB_HEX_RGBA");
for($i = 0; $i < count($validList); $i++) {
    if($validList[$i] == $cc) {
        $valid = true;
    }
}
if(! $valid) {
    $cc = "sRGB_HEX";
}

