</style>
<script src="https://ajax.googleapis.com/ajax/libs/jquery/1/jquery.min.js"></script>
<script>
    
    var colorCode = "<?php echo $cc; ?>";
    var color = [<?php echo $r; ?>, <?php echo $g; ?>, <?php echo $b; ?>, <?php echo $a; ?>];
    
    var lastMouseX = -1;
    var lastMouseY = -1;
    var elem;
    var dragging = false;
    var faded = false;
    
    $(document).ready(function() {
        createColorCode(colorCode);
        updateDisplay();
        function onMouseMove(x, y) {
            if(dragging) {
                if(!faded) {
                    $(".dragNotice").fadeOut(2500);
                    faded = true;
                }
                deselect();
                parseMousePos(event.pageX - lastMouseX, lastMouseY - event.pageY);
                lastMouseX = event.pageX;
                lastMouseY = event.pageY;
            }
        }
        function onMouseUp() {
            dragging = false;
        }
        $(document).mousemove(function(event) {
            onMouseMove(event.pageX, event.pageY);
        });
        $(document).mouseup(function(event) {
            onMouseUp();
        });
        $("html")[0].addEventListener('touchmove',function(event){ // for mobile phones
            onMouseMove(event.targetTouches[0].pageX, event.targetTouches[0].pageY);
        },false);
        $("html")[0].addEventListener('touchend',function(event){ // for mobile phones
            onMouseUp();
        },false);
        document.body.addEventListener('touchmove', function(e){ e.preventDefault(); },false); // for mobile
        document.getElementById("b_github").target = "_blank";
        
        $("#b_colorCode").hover(function() {
            $("#tooltip_colorCode").css("visibility", "visible");
        },function() {
            $("#tooltip_colorCode").css("visibility", "hidden");
        });
        $("#b_clipboard").hover(function() {
            $("#tooltip_clipboard").css("visibility", "visible");
        },function() {
            $("#tooltip_clipboard").css("visibility", "hidden");
        });
        $("#b_colorURL").hover(function() {
            $("#tooltip_colorURL").css("visibility", "visible");
        },function() {
            $("#tooltip_colorURL").css("visibility", "hidden");
        });
        $("#b_myColors").hover(function() {
            $("#tooltip_myColors").css("visibility", "visible");
        },function() {
            $("#tooltip_myColors").css("visibility", "hidden");
        });
        $("#b_addColor").hover(function() {
            $("#tooltip_addColor").css("visibility", "visible");
        },function() {
            $("#tooltip_addColor").css("visibility", "hidden");
        });
        $("#b_fullscreen").hover(function() {
            $("#tooltip_fullscreen").css("visibility", "visible");
        },function() {
            $("#tooltip_fullscreen").css("visibility", "hidden");
        });
        $("#b_github").hover(function() {
            $("#tooltip_github").css("visibility", "visible");
        },function() {
            $("#tooltip_github").css("visibility", "hidden");
        });
    });
    
    function createColorCode(cc) {
        colorCode = cc;
        if(colorCode == "sRGB_HEX") {
            $(".colorCode").html('<div style="height: 6em; font-size: 4em; line-height: 6em; margin-top: -3em;">#<span class="drag hex2" id="R_HEX">FF</span><span class="drag hex2" id="G_HEX">FF</span><span class="drag hex2" id="B_HEX">FF</span></div>');
        }
        if(colorCode == "sRGB_HEX_RGBA") {
            $(".colorCode").html('<div style="height: 6em; font-size: 4em; line-height: 6em; margin-top: -3em;">#<span class="drag hex2" id="R_HEX">FF</span><span class="drag hex2" id="G_HEX">FF</span><span class="drag hex2" id="B_HEX">FF</span><span class="drag hex2" id="A_HEX">FF</span></div>');
        }
        function onMouseDown(obj, x, y) {
            lastMouseX = x;
            lastMouseY = y;
            elem = obj;
            dragging = true;
        }
        $(".drag").each(function() {
            $(this).mousedown(function(event) {
                onMouseDown($(this), event.pageX, event.pageY);
            });
            $(this)[0].addEventListener('touchstart',function(event){ // for mobile phones
                onMouseDown($(this), event.targetTouches[0].pageX, event.targetTouches[0].pageY);
            },false);
        });
    }
    
    function parseMousePos(dx, dy) {
        var id = elem.attr("id");
        if(id == "R_HEX"){
            color[0] += dy;
            if(color[0] > 255) { color[0] = 255; }
            if(color[0] < 0) { color[0] = 0; }
        }
        if(id == "G_HEX"){
            color[1] += dy;
            if(color[1] > 255) { color[1] = 255; }
            if(color[1] < 0) { color[1] = 0; }
        }
        if(id == "B_HEX"){
            color[2] += dy;
            if(color[2] > 255) { color[2] = 255; }
            if(color[2] < 0) { color[2] = 0; }
        }
        if(id == "A_HEX"){
            color[3] += dy;
            if(color[3] > 255) { color[3] = 255; }
            if(color[3] < 0) { color[3] = 0; }
        }
        updateDisplay();
    }
    
    function updateDisplay() {
        $(".color").css("background-color", "rgba(" + color[0] + "," + color[1] + "," + color[2] + "," + (color[3] / 255) + ")");
        updateTextColor();
        $("#R_HEX").html(decToHex(color[0], 2));
        $("#G_HEX").html(decToHex(color[1], 2));
        $("#B_HEX").html(decToHex(color[2], 2));
        $("#A_HEX").html(decToHex(color[3], 2));
    }
    
    function updateTextColor() {
        var textColor = 0;
        var lightness = color[0] / 255 * 0.22 + color[1] / 255 * 0.35 + color[2] / 255 * 0.08 + (255 - color[3]) / 255 * 0.35;
        if(lightness <= 0.18) {
            textColor = 1;
        }
        
        var light = "#ffffff";
        var dark = "#171516";
        
        if(textColor > 0) {
            $(".colorCode").css("color", light);
            $(".dragNotice").css("color", light);
            $(".svg_stroke").attr("stroke", light);
            $(".svg_fill").attr("fill", light);
            $("#b_github").css("background-image", "url('?img=github_light')");
            $(".buttonTooltips > span").css("background-image", "url('?img=arrow_light')");
            $(".buttonTooltips > span span").css("background-color", light);
            $(".buttonTooltips > span span").css("color", dark);
        } else {
            $(".colorCode").css("color", dark);
            $(".dragNotice").css("color", dark);
            $(".svg_stroke").attr("stroke", dark);
            $(".svg_fill").attr("fill", dark);
            $("#b_github").css("background-image", "url('?img=github_dark')");
            $(".buttonTooltips > span").css("background-image", "url('?img=arrow_dark')");
            $(".buttonTooltips > span span").css("background-color", dark);
            $(".buttonTooltips > span span").css("color", light);
        }
    }
    
    function decToHex(n, l) {
        return leadingZeros(n.toString(16), l).toUpperCase();
    }
    
    function leadingZeros(num, length) {
        while(num.length < length) {
            num = "0" + num;
        }
        return num;
    }
    
    function deselect() {
        if (window.getSelection) {
            var selection = window.getSelection();                                        
            selection.removeAllRanges();
        }
        else {
            if (document.selection.createRange) {
                var range = document.selection.createRange();
                document.selection.empty();
            }
        }
    }
</script>
</head>
