                <line class="svg_stroke" x1="2" y1="16" x2="30" y2="16" stroke="#171516" stroke-width="2" stroke-linecap="round" />
                <line class="svg_stroke" x1="2" y1="23" x2="30" y2="23" stroke="#171516" stroke-width="2" stroke-linecap="round" />
